select   date(activity_date_time) as date, c.id campaign_id,  c.name, language_4 as lang, sum(case when activity_type_id=32 then 1 end) as total,  sum(case when a.status_id=2 and activity_type_id=32 then 1 end) as completed_existing_member,  sum(case when a.status_id=1 and activity_type_id=32 then 1 end) as pending,  sum(case when a.status_id=9 and activity_type_id=32 then 1 end) as completed_new_member,  sum(case when a.status_id=4 and activity_type_id=32 then 1 end) as optout,  sum(case when activity_type_id=54 then 1 end) as share,  sum(case when activity_type_id=56 then 1 end) as member_leave from civicrm_activity a join civicrm_campaign c on campaign_id=c.id join civicrm_value_speakout_integration_2 custom on entity_id=c.id left join civicrm_value_action_source_4 utm on a.id=utm.entity_id where activity_type_id in (32,54,56) and activity_date_time >= (DATE(NOW()) - INTERVAL 7 DAY) group by date, c.id order by date desc, completed_new_member desc
